import React from 'react'
import personService from '../services/persons'


const Persons = ( {persons, newFilter, setPersons} ) => {
    
    const DelPerson = (element) => {
        const message = `Delete ${element.name}`
        if (window.confirm(message)) {
            personService.delete(element.id).then(response => setPersons(persons.concat(response)))

            //personService.getAll().then(response => setPersons(response))
        }
    }
    
    return (
        <div>
            <table>
                <tbody>
                    {persons.map((element, index) => {
                        if (element.name.search(newFilter) !== -1 || element.name.length === 0)
                         return (
                            <tr key={index}>
                                <td>{element.name}</td>
                                <td>{element.number}</td>
                                <td><button onClick={() => DelPerson(element)}>Delete</button></td>
                            </tr>
                        )
                        else return null
                    })}
                </tbody>
            </table>
        </div>
    )
}

export { Persons }
