import React from "react";



const ShowPerson = ({persons, setPersons}) => {

const DelPerson = () => {
    const message = `Delete ${persons.name}`
    if (window.confirm(message)) {
        personService.delete(persons.id)
        personService.getAll().then(response => setPersons(response))
    }
}
    return (
        <tr key={persons.id}>
        <td>{persons.name}</td>
        <td>{persons.number}</td>
        <td><button onClick={DelPerson}>Delete</button></td>
    </tr>
    )
}

export { ShowPerson }
