const PersonForm = ( {persons, setPerson, newName, setNewName} ) => {

    const addContact = (event) => {
        event.preventDefault()    
        if (persons.find(value => value.name == newName)) {
            alert(newName + ' is already added to phonebook')
        } else {
            const personObject = {
                name: newName
              }
            setPerson(persons.concat(personObject))
            setNewName('')
        }
    }
    
    const handleContactChange = (event) => setNewName(event.target.value)

    return (
        <form onSubmit={addName}>
        <div>
            <div>
