                value={newName}
                onChange={handleNameChange}/>
            </div>
            <div>
                Phone number: <input 
                value={newNumber}
                onChange={handleContactChange}/>
            </div>
          
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
    ) 
}

