import React from 'react'
import axios from 'axios'
import personService from '../services/persons'

const PersonForm = ( {persons, setPerson, newName, setNewName, newNumber, setNewNumber} ) => {

    const addContact = (event) => {
        event.preventDefault()    
        if (persons.find(value => value.name == newName)) {
            alert(newName + ' is already added to phonebook')
        } else {
            const contactObject = {
                name: newName,
                number: newNumber
              }
            personService.create(contactObject).
            then(response => {
                setPerson(persons.concat(contactObject))
                setNewName('')
                setNewNumber('')    
            })            
        }
    }
    
    const handleChange = (setValue) => (event) => setValue(event.target.value)
  
    return (
        <form onSubmit={addContact}>
        <table>
            <body>
                Name: <input 
                value={newName}
                onChange={handleChange(setNewName)}/>
            
                Phone number: <input 
                value={newNumber}
                onChange={handleChange(setNewNumber)}/>
            </body>
          
        </table>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
    ) 
}

export { PersonForm }
