import React from 'react'
import personService from '../services/persons'

const PersonForm = ( {persons, setPersons, newName, setNewName, newNumber, setNewNumber} ) => {

    const validateContact = (event) => {
        event.preventDefault()
        if (persons.find(value => value.name == newName)) {
            const updContact = persons.filter(person => person.name === newName)
            if (window.confirm(newName + ' is already to the phonebook, replace de old number with a new one?') ) {
                updateContact(updContact[0].id)
            }
        } else {
            addContact()
        }
    }

    const addContact = () => {
        //event.preventDefault()    
        //if (persons.find(value => value.name == newName)) {
            //alert(newName + ' is already added to phonebook')
        //} else {
            const contactObject = {
                name: newName,
                number: newNumber
            }
            personService.create(contactObject).
            then(response => {
                setPersons(persons.concat(response))
                setNewName('')
                setNewNumber('')    
            })            
        //}
    }

    const updateContact = (id) => {
        const contactObject = {
            name: newName,
            number: newNumber
        }
        console.log('id: ', id, 'contactObject: ', contactObject)
        personService.update(id, contactObject)
        personService.getAll().then(response => setPersons(response.filter(person => person.id === id)))
        setNewName('')
        setNewNumber('')
    }
     
    const handleChange = (setValue) => (event) => setValue(event.target.value)
  
    return (
        <form onSubmit={validateContact}>
        <table>
            <tbody>
                <tr>
                    <td>Name:</td>
                    <td>
                        <input 
                        value={newName}
                        onChange={handleChange(setNewName)}/>
                    </td>
                </tr>
                <tr>
                    <td>Phone number:</td>
                    <td>
                        <input 
                        value={newNumber}
                        onChange={handleChange(setNewNumber)}/>    
                    </td>
                </tr>
                <tr><td><button type="submit">add</button></td></tr>
            </tbody>          
        </table>
      </form>
    ) 
}

export { PersonForm }
