import { useEffect } from 'react'
import React from 'react'
import personService from '../services/persons'


const Persons = ( {persons, newFilter, setPersons} ) => {
    
    const delperson = (element) => {
        const message = `Delete ${element.name}`
        if (window.confirm(message)) {
            personService.delete(element.id)
            useEffect(() => {
                personService.getAll().then(response => {
                  setPersons(response)
                })
              }, [])
        }
    }
    
    return (
        <div>
            <table>
                <tbody>
                    {persons.map((element, index) => {
                        if (element.name.search(newFilter) !== -1) return (
                            <tr key={index}>
                                <td>{element.name}</td>
                                <td>{element.number}</td>
                                <td><button onClick={() => delperson(element)}>Delete</button></td>
                            </tr>
                        )
                        else return null
                    })}
                </tbody>
            </table>
        </div>
    )
}

export { Persons }
